body.messageBody:
{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "TextBlock",
            "weight": "Bolder",
            "text": "Below URL(s) is/are blocked",
            "wrap": true
        },
    {
  "separator": "true",
  "type": "ColumnSet",
  "width": "stretch"
},
{
  "columns": [
    {
      "items": @{body('select_all_invalid_URL')},
      "type": "Column",
  "wrap": true
    }
  ],
  "type": "ColumnSet"
},
 {
    "text": " Incident#: @{triggerBody()?['object']?['properties']?['incidentNumber']} ",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
        {
            "type": "TextBlock",
            "text": "[Click here to view the Incident](@{triggerBody()?['object']?['properties']?['incidentUrl']})",
            "wrap": true
        },

        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "TextBlock",
                            "size": "Medium",
                            "weight": "Bolder",
                            "text": "Incident Configuration:",
                            "wrap": true
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "Image",
                            "style": "Person",
                            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",
                            "size": "Small"
                        }
                    ],
                    "width": "auto"
                }
            ]
        },
        {
            "type": "TextBlock",
            "text": "Close Azure Sentinal incident?"
        },
        {
            "choices": [
                {
                    "isSelected": true,
                    "title": "False Positive - Inaccurate Data",
                    "value": "False Positive - Inaccurate Data"
                },
                {
                    "isSelected": true,
                    "title": "False Positive - Incorrect Alert Logic",
                    "value": "False Positive - Incorrect Alert Logic"
                },
                {
                    "title": "True Positive - Suspicious Activity",
                    "value": "True Positive - Suspicious Activity"
                },
                {
                    "title": "Benign Positive - Suspicious But Expected",
                    "value": "Benign Positive - Suspicious But Expected"
                },
                {
                    "title": "Undetermined",
                    "value": "Undetermined"
                }
            ],
            "id": "incidentStatus",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "Benign Positive - Suspicious But Expected"
        },
        {
            "type": "TextBlock",
            "text": "Change Azure Sentinel Incident Severity?"
        },
        {
            "choices": [
                {

                    "title": "High",
                    "value": "High"
                },
                {
                    "title": "Medium",
                    "value": "Medium"
                },
                {
                    "title": "Low",
                    "value": "Low"
                },
                {
                    "title": "Don't change",
                    "value": "same"
                }
            ],
            "id": "incidentSeverity",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "@{triggerBody()?['object']?['properties']?['severity']}"
        }



    ],
"width":"auto",
   "actions": [
                    {
                        "type": "Action.Submit",
                        "title": "Change Incident Configuration"
                    },
  {
                        "type": "Action.Submit",
                        "title": "Ignore"
                    }
   ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}
